export CURDESK=$(i3-msg -t get_workspaces | jq -r '.[] | select(.visible) | select(.focused) | .output'); i3-msg -t get_workspaces | jq -r '[.[] | select(.output == env.CURDESK)] | max_by(.num) | .num += 1 | if .num==11then"1one" elif .num==1then"1one" elif .num==2then"2two" elif .num==3then"3three" elif .num==4then"4four" elif .num==5then"5five" elif .num==6then"6six" elif .num==7then"7seven" elif .num==8then"8eight" elif .num==9then"9nine" elif .num==10then"10ten" else "test" end' | xargs -0 -I% sh -c 'i3-msg move container to workspace number %  i3-msg workspace %' 2>&1 1> /dev/null
